// src/components/HotelBooking.js
import React, { useState } from 'react';

function HotelBooking() {
  const [hotelCriteria, setHotelCriteria] = useState({
    rooms: 1,
    type: 'Standard',
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    // Logique de réservation d'hôtel
    console.log('Réservation d\'hôtel:', hotelCriteria);
  };

  return (
    <div>
      <h2>Réservation d'Hôtel</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="number"
          placeholder="Nombre de chambres"
          value={hotelCriteria.rooms}
          onChange={(e) => setHotelCriteria({ ...hotelCriteria, rooms: e.target.value })}
          min="1"
          required
        />
        <select
          value={hotelCriteria.type}
          onChange={(e) => setHotelCriteria({ ...hotelCriteria, type: e.target.value })}
        >
          <option value="Standard">Standard</option>
          <option value="Deluxe">Deluxe</option>
          <option value="Suite">Suite</option>
        </select>
        <button type="submit">Réserver l'hôtel</button>
      </form>
    </div>
  );
}

export default HotelBooking;
