import React, { useState } from "react";
import FlightBooking from "./components/FlightBooking";
import HotelBooking from "./components/HotelBooking";
import TotalAmount from "./components/TotalAmount";

function App() {
  const [flightData, setFlightData] = useState(null);
  const [hotelData, setHotelData] = useState(null);
  const [total, setTotal] = useState(null);

  const handleFlightSubmit = async (data) => {
    try {
      const response = await fetch('http://localhost:5001/flight_book', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });
      const result = await response.json();
      setFlightData(result);
    } catch (error) {
      console.error("Error booking flight:", error);
    }
  };

  const handleHotelSubmit = async (data) => {
    try {
      const response = await fetch('http://localhost:5002/hotel_book', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });
      const result = await response.json();
      setHotelData(result);
    } catch (error) {
      console.error("Error booking hotel:", error);
    }
  };

  const handleTotalCalculation = async () => {
    try {
      const response = await fetch('http://localhost:5003/calculate_total', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ flight: flightData, hotel: hotelData }),
      });
      const result = await response.json();
      setTotal(result.total_price);
    } catch (error) {
      console.error("Error calculating total:", error);
    }
  };

  return (
    <div className="App">
      <h1>Agence de Voyage - RÃ©servation</h1>
      <FlightBooking onSubmit={handleFlightSubmit} />
      <HotelBooking onSubmit={handleHotelSubmit} />
      {flightData && hotelData && (
        <button onClick={handleTotalCalculation}>Calculer le montant total</button>
      )}
      {total && <TotalAmount total={total} />}
    </div>
  );
}

export default App;
