// src/components/TotalAmount.js
import React, { useState } from 'react';

function TotalAmount() {
  const [totalAmount, setTotalAmount] = useState(0);

  const calculateTotal = () => {
    // Logique pour calculer le montant total
    // Exemple d'addition de montants de vol et d'hôtel (remplacez par votre logique)
    const flightAmount = 500; // Montant de vol
    const hotelAmount = 300; // Montant d'hôtel
    setTotalAmount(flightAmount + hotelAmount);
  };

  return (
    <div>
      <h2>Montant Total</h2>
      <button onClick={calculateTotal}>Calculer le montant total</button>
      <p>Montant total à payer: {totalAmount} €</p>
    </div>
  );
}

export default TotalAmount;
